function aLotOfOperation(){echo "entrer 2 valeur superieur a 0\n";$size=readline();$size3=readline();$a=pow(2,24)-1; /* on set a un int 24bit*/while($a!=0){$a=$a-1;}while($a!=$size){$a=$a+(pow(2,24)-1)/(pow(2,24)-1);}$b=(pow(2,24)-1);while($b!=0){$b=$b-1;}while($b!=$size3){$b=$b+(pow(2,24)-1)/(pow(2,24)-1);}for($i = 0 ; $i < $a * $b  ; $i++){$a^=$b;$b^=$a;$a^=$b;}$d = 0;$a = abs($a);$b = abs($b);$nbr = $a;$nbr2 = $b;$i = 1;$i2 = 1;$k = 0;while($nbr >= 2){$nbr = intdiv($nbr,2);$i = $i +1;}while($nbr2 >= 2){$nbr2 = intdiv($nbr2,2);$i2 = $i2 +1;}if($i >= $i2){$k = $i;}else{$k = $i2;}$Chaine=array($k +1);$Chaine2=array($k +1);$k = 0;while($a >= 2){$Chaine[$k] = $a % 2;$a = intdiv($a,2);$k = $k + 1;}$Chaine[$k] = $a/$a;$k = 0;while($b >= 2){$Chaine2[$k] = $b % 2;$b = intdiv($b,2);$k = $k + 1;}$Chaine2[$k] = $b/$b;$Chaine3=array($k + 2);$retenu = 0;for($i = 0 ; $i <= $k ; $i++){if($Chaine[$i] + $Chaine2[$i] + $retenu == 3){$Chaine3[$i] = 1;$retenu = 1;}    else if($Chaine[$i] + $Chaine2[$i] + $retenu == 2){$Chaine3[$i] = 0;$retenu = 1;}else if($Chaine[$i] + $Chaine2[$i] + $retenu == 1){$Chaine3[$i] = 1;$retenu = 0;}else if($Chaine[$i] + $Chaine2[$i] + $retenu == 0){$Chaine3[$i] = 0;$retenu = 0;}}$Chaine3[$k+1] = $retenu;$d = 0;for($i = 0 ; $i <= $k+1 ; $i++){$d = $d + $Chaine3[$i] * pow(2,$i);}$d = strval($d);$o==strlen($d)-1;$s="";for($i=0;$i<strlen($d);$i++){$m=strlen($d)-1-$i;$m=$d[$m];if($m=="1"){$s=$s."一";}if($m=="2"){$s=$s."二";}if($m=="3"){$s=$s."三";}if($m=="4"){$s=$s."四";}if($m=="5"){$s=$s."五";}if($m=="6"){$s=$s."六";}if($m=="7"){$s=$s."七";}if($m=="8"){$s=$s."八";}if($m=="9"){$s=$s."九";}}echo $s;}aLotOfOperation();
